CREATE TABLE PRODUCTO (CODIGO_BARRAS VARCHAR NOT NULL, DESCRIPCION VARCHAR, NOMBRE VARCHAR, PRIMARY KEY (CODIGO_BARRAS))
CREATE TABLE LOTE (ID BIGINT NOT NULL, CANTIDAD DOUBLE, CODIGOLOTE VARCHAR, CONSUMO_PREFERENTE DATE, FABRICACION DATE, PRIMARY KEY (ID))
CREATE TABLE PRODUCTO_LOTE (Producto_CODIGO_BARRAS VARCHAR NOT NULL, lotes_ID BIGINT NOT NULL, PRIMARY KEY (Producto_CODIGO_BARRAS, lotes_ID))
ALTER TABLE PRODUCTO_LOTE ADD CONSTRAINT FK_PRODUCTO_LOTE_lotes_ID FOREIGN KEY (lotes_ID) REFERENCES LOTE (ID)
ALTER TABLE PRODUCTO_LOTE ADD CONSTRAINT FK_PRODUCTO_LOTE_Producto_CODIGO_BARRAS FOREIGN KEY (Producto_CODIGO_BARRAS) REFERENCES PRODUCTO (CODIGO_BARRAS)
CREATE TABLE SEQUENCE (SEQ_NAME VARCHAR(50) NOT NULL, SEQ_COUNT NUMERIC(38), PRIMARY KEY (SEQ_NAME))
INSERT INTO SEQUENCE(SEQ_NAME, SEQ_COUNT) values ('SEQ_GEN', 0)
